mapper.update:
update course
    set  ${column} = ${column} + #{incr}
where id in #{item}

mapper.select:
SELECT id, alias, title, introduction, banner, type, price, is_discount, discount_price, learn_persons, audit_status, created_at FROM course c WHERE audit_status = 1 AND type = #{paramsMap.type}
AND ( title LIKE CONCAT( '%', #{paramsMap.keyword}, '%' ) OR introduction LIKE CONCAT( '%', #{paramsMap.keyword}, '%' ) OR EXISTS ( SELECT teacher_id, course_id FROM teacher_course tc WHERE tc.teacher_id IN ( SELECT id FROM teachers t
WHERE t.`name` LIKE CONCAT( '%', #{paramsMap.keyword}, '%' ) ) AND tc.course_id = c.id))
AND EXISTS ( SELECT category_id, course_id FROM course_category cc WHERE cc.category_id IN ( SELECT id FROM category cy
and cy.id = #{paramsMap.label}
and parent_id = #{paramsMap.category}
)
and cc.course_id =c.id )
ORDER BY
id DESC
learn_persons desc,id DESC

SELECT id, alias, title, introduction, banner, type, price, is_discount, discount_price, learn_persons, audit_status, created_at FROM course c WHERE audit_status = 1
AND type = #{type}
AND EXISTS(select user_id, course_id from user_course uc where uc.user_id = #{userId} and uc.course_id = c.id) order by type desc,id desc

SELECT id, alias, title, introduction, bg_img, banner, tip, learn_what, type, price, is_discount, discount_price, is_finish, learn_persons, audit_status, audit_notice, created_at FROM course c WHERE 1 = 1
AND type = #{paramsMap.type}
AND audit_status = #{paramsMap.auditStatus}
AND is_discount = #{paramsMap.isDiscount}
AND ( title LIKE CONCAT( '%', #{paramsMap.keyword}, '%' ) OR introduction LIKE CONCAT( '%', #{paramsMap.keyword}, '%' ))
AND EXISTS ( SELECT teacher_id, course_id FROM teacher_course tc WHERE tc.teacher_id = #{paramsMap.teacherId} AND tc.course_id = c.id )
ORDER BY id desc
